function windowToCanvas(t,e,n){var o=t.getBoundingClientRect();return{x:e-o.left*(t.width/o.width),y:n-o.top*(t.height/o.height)}}function drawBackground(){var t=context.canvas.height;for(context.clearRect(0,0,canvas.width,canvas.height),context.strokeStyle="lightgray",context.lineWidth=.5;t>48;)context.beginPath(),context.moveTo(0,t),context.lineTo(context.canvas.width,t),context.stroke(),t-=12}function drawSpritesheet(){context.drawImage(spritesheet,0,0)}function drawGuidelines(t,e){context.strokeStyle="rgba(0, 0, 230, 0.8)",context.lineWidth=.5,drawVerticalLine(t),drawHorizontalLine(e)}function updateReadout(t,e){readout.innerText="("+t.toFixed(0)+","+e.toFixed(0)+")"}function drawHorizontalLine(t){context.beginPath(),context.moveTo(0,t+.5),context.lineTo(context.canvas.width,t+.5),context.stroke()}function drawVerticalLine(t){context.beginPath(),context.moveTo(t+.5,0),context.lineTo(t+.5,context.canvas.height),context.stroke()}var readout=document.getElementById("readout"),spritesheet=new Image;canvas.onmousemove=function(t){var e=windowToCanvas(canvas,t.clientX,t.clientY);drawBackground(),drawSpritesheet(),drawGuidelines(e.x,e.y),updateReadout(e.x,e.y)},spritesheet.src="image/cjml.png",spritesheet.onload=function(t){drawSpritesheet()},drawBackground();